import { Link } from 'react-router-dom';  // Import Lintk for navigation

import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import Modal from "react-modal";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "jspdf-autotable"; // Import the autoTable
Modal.setAppElement("#root");

function StudentDashboard() {
  const { user, logout } = useAuth();
  const [outpassRequests, setOutpassRequests] = useState([]);
  const [fromTime, setFromTime] = useState("");
  const [toTime, setToTime] = useState("");
  const [error, setError] = useState("");
  const [reason, setReason] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [activeTab, setActiveTab] = useState("pending");
  const [selectedOutpass, setSelectedOutpass] = useState(null);
  const [profileView, setProfileView] = useState(false);

  const apiUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    const fetchOutpassHistory = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(`${apiUrl}/api/student/outpass-history`, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        setOutpassRequests(data.outpasses);
      } catch (err) {
        console.error(err);
      }
    };

    fetchOutpassHistory();
  }, [apiUrl]);

 const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate the 'From Time' and 'To Time'
    if (new Date(fromTime) >= new Date(toTime)) {
      setError("From time must be earlier than To time.");
      return;
    }
  };
  const approvedOutpasses = outpassRequests.filter(op => op.status === "approved");
  const pendingOutpasses = outpassRequests.filter(op => op.status === "pending");
  const pastOutpasses = outpassRequests.filter(op => new Date(op.toTime) < new Date());

  const renderOutpassList = () => {
    let outpasses = [];
    let label = "";

    switch (activeTab) {
      case "approved":
        outpasses = approvedOutpasses;
        label = "Approved Outpasses";
        break;
      case "pending":
        outpasses = pendingOutpasses;
        label = "Pending Outpasses";
        break;
      case "past":
        outpasses = pastOutpasses;
        label = "Past Outpasses";
        break;
      default:
        break;
    }

    return (
      <div>
        <h3 className="text-xl font-semibold text-gray-700 mb-4">{label}</h3>
        <div className="h-72 overflow-y-auto space-y-4">
          {outpasses.length > 0 ? (
            outpasses.map((outpass) => (
              <div
                key={outpass.id}
                className="bg-gray-100 p-4 rounded-xl shadow-md border border-gray-300 hover:shadow-lg transition duration-300"
              >
                <p><strong>From:</strong> {new Date(outpass.fromTime).toLocaleString()}</p>
                <p><strong>To:</strong> {new Date(outpass.toTime).toLocaleString()}</p>
                <p><strong>Reason:</strong> {outpass.reason}</p>
                <p className="mt-1">
                  <strong>Status:</strong>{" "}
                  <span className={`px-3 py-1 rounded-full text-sm font-medium uppercase ${
                    outpass.status === "approved"
                      ? "bg-green-200 text-green-800"
                      : outpass.status === "pending"
                      ? "bg-yellow-200 text-yellow-800"
                      : "bg-red-200 text-red-800"
                  }`}>
                    {outpass.status}
                  </span>
                </p>
                <div className="flex justify-end gap-3 mt-3">
                  <button
                    onClick={() => setSelectedOutpass(outpass)}
                    className="text-blue-600 hover:text-blue-800 transition font-medium"
                  >
                    View
                  </button>
                  <button
                    onClick={() => generatePDF(outpass)}
                    className="text-green-600 hover:text-green-800 transition font-medium"
                  >
                    Download PDF
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p className="text-gray-500">No {label.toLowerCase()}.</p>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="bg-gradient-to-br from-blue-100 to-white min-h-screen p-4 sm:p-8">
      <div className="bg-blue-600 text-white py-4 px-6 rounded-lg text-center shadow-md mb-8">
        <h1 className="text-2xl sm:text-3xl font-bold">Welcome, {user?.name}</h1>
      </div>

      {/* Header Buttons */}
      <div className="flex flex-col sm:flex-row justify-end items-center gap-4 mb-6">
        <button
          onClick={() => setProfileView(!profileView)}
          className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-5 rounded-full transition"
        >
          {profileView ? "Hide Profile" : "Show Profile"}
        </button>

        <Link to="/">
          <button className="bg-indigo-500 hover:bg-indigo-600 text-white font-semibold py-2 px-5 rounded-full transition">
            Back to Home
          </button>
        </Link>

        <button
          onClick={logout}
          className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-5 rounded-full transition"
        >
          Logout
        </button>
      </div>

      {/* Profile Section */}
      {profileView && (
        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Profile</h2>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
          <p><strong>Rollno:</strong> {user?.Rollno}</p>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Outpass Form */}
        <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Request Outpass</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block font-medium text-gray-600 mb-1">From Time</label>
              <input
                type="datetime-local"
                value={fromTime}
                onChange={(e) => setFromTime(e.target.value)}
                className="w-full border border-gray-300 p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block font-medium text-gray-600 mb-1">To Time</label>
              <input
                type="datetime-local"
                value={toTime}
                onChange={(e) => setToTime(e.target.value)}
                className="w-full border border-gray-300 p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block font-medium text-gray-600 mb-1">Reason</label>
              <textarea
                value={reason}
                onChange={(e) => {
                  if (e.target.value.length <= 30) setReason(e.target.value);
                }}
                className="w-full border border-gray-300 p-2 rounded-lg"
                rows="2"
                required
              />
              <p className="text-sm text-gray-500">{30 - reason.length} characters remaining</p>
            </div>

            {error && <p className="text-red-500 text-sm">{error}</p>}
            {successMessage && <p className="text-green-600 text-sm">{successMessage}</p>}

            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-600 w-full text-white py-2 rounded-full font-semibold transition"
            >
              Submit Request
            </button>
          </form>
        </div>

        {/* Status Viewer */}
        <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Entrypass Status</h2>
          <div className="flex justify-between mb-4">
            {["pending", "approved", "past"].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-4 py-2 w-full rounded-full mx-1 transition ${
                  activeTab === tab
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-800"
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            ))}
          </div>
          {renderOutpassList()}
        </div>
      </div>

      {/* Modal remains unchanged */}
      {selectedOutpass && (
        <Modal
          isOpen={!!selectedOutpass}
          onRequestClose={() => setSelectedOutpass(null)}
          className="bg-white p-6 rounded-lg max-w-xl mx-auto mt-20 shadow-xl"
          overlayClassName="fixed inset-0 bg-black bg-opacity-50"
        >
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Outpass Details</h2>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Rollno:</strong> {user.Rollno}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>From:</strong> {new Date(selectedOutpass.fromTime).toLocaleString()}</p>
          <p><strong>To:</strong> {new Date(selectedOutpass.toTime).toLocaleString()}</p>
          <p><strong>Reason:</strong> {selectedOutpass.reason}</p>
          <p><strong>Status:</strong> {selectedOutpass.status}</p>

          <button
            onClick={() => setSelectedOutpass(null)}
            className="mt-6 bg-red-500 hover:bg-red-600 text-white py-2 px-4 w-full rounded-lg transition"
          >
            Close
          </button>
        </Modal>
      )}
    </div>
  );
}

export default StudentDashboard;
